CREATE TABLE User(id BIGINT NOT NULL PRIMARY KEY AUTO_INCREMENT, login VARCHAR(30) NOT NULL UNIQUE, email VARCHAR(50) NOT NULL UNIQUE, password VARCHAR(256) NOT NULL, rating BIGINT NOT NULL DEFAULT 0);
CREATE TABLE Session(id BIGINT NOT NULL PRIMARY KEY AUTO_INCREMENT, session VARCHAR(50) NOT NULL UNIQUE, userId BIGINT NOT NULL, creationTime DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP());
CREATE TABLE Plane(id INT NOT NULL PRIMARY KEY AUTO_INCREMENT, name VARCHAR(20) NOT NULL UNIQUE, src VARCHAR(50) NOT NULL);
CREATE TABLE Airport(id INT NOT NULL PRIMARY KEY AUTO_INCREMENT, title VARCHAR(50) NOT NULL UNIQUE, src VARCHAR(50) NOT NULL);